    private void hitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungActionPerformed
        // TODO add your handling code here:
        double inBB = Integer.valueOf(BB.getText());
        double inTB = Integer.valueOf(TB.getText());
        double TBM = inTB/100;
        double TBH = TBM*TBM;
        double ouHitung = inBB/TBH;
        
        if (ouHitung > 24){
        Cetak.setText(", Nama"+Nm.getText()+", dengan NIM"+Nim.getText() +", "+ "Berat Badan Ideal");
    }   else if  (ouHitung < 18.5){
            Cetak.setText(", Nama"+Nm.getText()+", dengan NIM"+Nim.getText() +", "+ "Berat Badan Ideal");
    }   else {
        Cetak.setText(", Nama"+Nm.getText()+", dengan NIM"+Nim.getText() +", "+ "Berat Badan Belum Ideal");
    }       
    }//GEN-LAST:event_hitungActionPerformed

